// @require @packageOverrides
Ext.Loader.setConfig({
    enabled: true,
    paths: {
        'Ext.ux': './app/ux'
    }
});


Ext.application({

    requires: [
        'Ext.Loader',
        'Camminus.util.Alert',
        'Camminus.util.Util',
        'Ext.layout.container.Absolute',
        'Ext.ux.statusbar.StatusBar',
        'Ext.layout.container.Border',
        'Ext.layout.container.Accordion',
        'Ext.ux.TextMaskPlugin'
    ],
    splashscreen: {
        
    },
    stores: [
        'cases.Tipo',
        'cases.Previsao',
        'cases.TipoEquip'
    ],
    views: [
        'MainView',
        'cases.lab.Opened'
    ],
    controllers: [
        'Users',
        'TranslationManager',
        'Menu',
        'Roles',
        'Costumers',
        'Cases',
        'Companies',
        'Services',
        'Main'
    ],
    name: 'Camminus',

    launch: function() {
        var task = new Ext.util.DelayedTask(function() {
            splashscreen.fadeOut({
                duration: 1000,
                remove:true
            });

            splashscreen.next().fadeOut({
                duration: 1000,
                remove:true,
                listeners: {

                    afteranimate: function(el, startTime, eOpts) {
                        Ext.Ajax.request({
                            url: '/login',
                            success: function(response){

                                var retorno = Ext.JSON.decode(response.responseText);

                                if(retorno.success){

                                    Ext.create('Camminus.view.MainView');
                                }
                                else{
                                    Ext.create('Camminus.view.Login');
                                }

                            },

                            failure: function(response) {
                                Ext.Msg.show({
                                    title:' Erro!',
                                    msg: response.responseText,
                                    icon: Ext.Msg.ERROR,
                                    buttons: Ext.Msg.OK
                                });

                            }

                        });

                    }

                }

            });

        });


        Ext.define( 'UserData', {

            singleton: true,

            config:{
                name: undefined,
                id: undefined,
                role_id: undefined
            },

            contructor: function(cfg){
                this.iniConfig(cfg);
            }

        });

        task.delay(2000);
    },

    init: function() {
        splashscreen = Ext.getBody().mask(translations.loading_app, 'splashscreen');
        splashscreen.addCls('splashscreen');

        Ext.DomHelper.insertFirst(Ext.query('.x-mask-msg')[0], {
            cls: 'x-splash-icon'
        });
    }

});
